import React from "react";
import Banner from "./home/Banner";
import Contents from "./home/Contents";
import styled from 'styled-components'
import { Link } from "react-router-dom";
import { Content, Maincontents, Bigcontents,Contentt } from "./Maincontents";
import "./home/contents.css";
import Slider from "react-slick";
import ImgSlider from "./components/ImgSlider";
var projects = [
  { src: "https://i.imgur.com/3IzVsfz.png", title: "My Portfolio", alt: "project 1", about: "I created my personal portfolio with Next.js and Tailwind CSS. I used React Slider for this project carsouel.", link: "https://sumitpanwar10.github.io/MyPortfolio/" },
  { src: "https://i.imgur.com/NxdB6Sd.png", title: "Curl Counter", alt: "project 2", about: "OpenCV based Curl Counter using Mediapipe Pose solution. Made in Juypter Notebook.", link: "https://github.com/sumitpanwar10/CurlCounter/" },
  { src: "https://i.imgur.com/G874ExV.png", title: "Notes Keeper", alt: "project 3", about: "Notes Keeper web application keeps the notes. The project is built with React . I've used localStorage to store data locally.", link: "https://sumitpanwar10.github.io/Notes-keeper/" },
  { src: "https://i.imgur.com/ouMDILh.png", title: "To Do List", alt: "project 4", about: "The To-Do-List is build with React using userState hooks. It was my first project on React.", link: "https://sumitpanwar10.github.io/To-Do-List/" },
  { src: "https://i.imgur.com/NJa2Vic.png", title: "Score Keeper", alt: "project 5", about: " I wrote code for a Scorekeeper using JS and Bulma. It keeps score track of two players. It has a feature to set the winning point and a reset button to reset the whole application.", link: "https://sumitpanwar10.github.io/Score-keeper/" },
  { src: "https://i.imgur.com/vfgzibn.png", title: "BMW Landing Page", alt: "project 6", about: "This is a BMW landing page built with HTML and CSS. Also, I designed it in Figma.", link: "https://sumitpanwar10.github.io/BMW-m8-landing-page/" },
]
class Home extends React.Component {

  state = {
    maincontent: Maincontents,
    bigcontent: Bigcontents,
    contents: Content,
    contentss: Contentt,
  };
  render() {
    return (
      <div>
        <Banner />
        <br></br>
        <div className="Lool">
          <h2>Top three attractions</h2>
        </div>

        <div className="home__cont">
          {this.state.maincontent.map((item) => {
            return (
              <div key={item.id}>
                <Contents
                  id={item.id}
                  img={item.img}
                  title={item.title}
                  description={item.description}
                />
              </div>
            );
          })}
        </div>
        <div className="Lool">
          <h2>Extraordinary Reasons to Visit ODISHA</h2>
        </div>
        <Container>
          <ImgSlider/>
        </Container>
        <div className="Lool">
          <h2>Popular Packages</h2>
        </div>
        <div className="home__cont">

          {this.state.contents.map((item) => {
            return (
              <div key={item.id}>
                <Link to={`/result/${item.id}`} className="link">
                  <Contents
                    id={item.id}
                    img={item.img}
                    title={item.title}
                    amount={item.price}
                    description={item.description}
                  />
                </Link>
              </div>
            );
          })}
        </div>
        <div className="Lool">
          <h2>Cuisine</h2>
        </div>

        <Slider

          dots={false}
          slidesToShow={3}
          slidesToScroll={3}
          autoplay={true}
          autoplaySpeed={3000}
          responsive={[
            {
              breakpoint: 1024,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,
                infinite: true,

              }
            },
            {
              breakpoint: 600,
              settings: {
                slidesToShow: 2,
                slidesToScroll: 2,

              }
            },
            {
              breakpoint: 480,
              settings: {
                slidesToShow: 1,
                slidesToScroll: 1
              }
            }
          ]}
        >
          {projects.map((project, index) => {
            return (
              <div key={index} className="flex wrap" >
                <div className="flex justify-center min-h-full">
                  <div className="rounded-lg  shadow-[0px_3px_10px_rgba(0,0,0,0.1)] bg-white max-w-sm m-6  dark:bg-gray-800 ">
                    <a href="#!">
                      <img className="rounded-t-lg" src={project.src} alt="" />
                    </a>
                    <div className="p-6 h-1/5">
                      <h5 className=" text-teal-600 text-xl font-medium mb-2  dark:text-teal-400">{project.title}</h5>
                      <div className='min-h-max'>
                        <p className='text-gray-800 py-1  h-48  text-base mb-4 dark:text-gray-300'>
                          {project.about}
                        </p>
                      </div>
                      <a href={project.link} target="_blank">
                        <button type="button" className="uppercase text-sm font-bold tracking-wide  bg-teal-600 text-gray-100 p-3 rounded-lg w-full focus:outline-none focus:shadow-outline hover:scale-110 transition duration-300 ease-in-out">View</button>
                      </a>
                    </div>
                  </div>
                </div>
              </div>)
          })}
        </Slider>
        <div className="home__cont">
          {this.state.contentss.map((item) => {
            return (
              <div key={item.id}>
                <Link to={`/result/${item.id}`} className="link">
                  <Contents
                    id={item.id}
                    img={item.img}
                    title={item.title}
                    amount={item.price}
                    description={item.description}
                  />
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
export default Home;

const Container = styled.main`
    height: 500px; /* set height to match the height of the images in the slider */
    width: 100%; /* set width to 100% to match the width of the image slider */
    padding: 0 calc(3.5vw + 5px);
    position: relative;
    overflow-x: hidden;
    &:before{
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: -1;
        background-size: cover; /* add this to make sure the background image fills the entire container */
    }
`
