import React from "react";
import Ranger from "./home/Ranger";
import "./Header.css";
import "./home/Navbar.css";
import styled from "styled-components";
import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';

// Your Firebase configuration
const firebaseConfig = {
  apiKey: "AIzaSyCn9RAQo0EnCDgSZglI79RvZ0VJG8wPZBA",
  authDomain: "odisha-a56eb.firebaseapp.com",
  projectId: "odisha-a56eb",
  storageBucket: "odisha-a56eb.appspot.com",
  messagingSenderId: "1028398274530",
  appId: "1:1028398274530:web:57b8d5f4b0c3246aa90bbb",
  measurementId: "G-Q9R90FZLF4"
};
// Initialize Firebase
firebase.initializeApp(firebaseConfig);

class Header extends React.Component {
  state = {
    isclicked: false,
  };

  sel = () => {
    this.setState({
      isclicked: !this.state.isclicked,
    });
  };

  handleLogin = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    firebase
      .auth()
      .signInWithPopup(provider)
      .then((result) => {
        console.log(result)
      })
      .catch((error) => {
        console.error(error);
      });
  };
  render() {
    return (
      <div className="header">
        {this.state.isclicked && (
          <div>
            <Ranger />
          </div>
        )}
        <span>

          <NavMenu>
            <h3>Discover Secrets At Every Corner</h3>
            <Login onClick={this.handleLogin}>Login</Login>
          </NavMenu>

        </span>
      </div>
    );
  }
}
export default Header;
const NavMenu = styled.div`
    display: flex;
    flex: 1;
    margin-left: 25px;
    align-items: center;
    a{
        display: flex;
        align-items: center;
        padding: 0 12px;
        cursor: pointer;
        img{
            height: 20px;
        }
        span{
            font-size: 13px;
            letter-spacing: 1.42px;
            position: relative;
            &:after{
                content: "";
                height: 2px;
                background: white;
                position: absolute;
                left: 0;
                right: 0;
                bottom: -6px;
                opacity: 0;
                transform-origin: left center;
                transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
                transform: scaleX(0);
            }
        }
        &:hover{
            span:after{
                transform: scaleX(1);
                opacity: 1;
            }
        }
    }
`
const Login = styled.a`
  background-color: rgba(0,0,0,0.6);
  padding: 8px 16px;
  display: flex;
  justify-content: flex-end;
  position: fixed;
  right: 0;
  text-transform: uppercase;
  letter-spacing: 1.5px;
  border: 1px solid #f9f9f9;
  border-radius: 4px;
  transition: all 0.2s ease 0s;
  cursor: pointer;
  &:hover{
    background-color: #f9f9f9;
    color: #000;
    border-color: transparent;
  }
`
