import React from 'react'
import styled from 'styled-components'
import Slider from 'react-slick'
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
function ImgSlider() {
    let settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true
    }

  return (
    <Carousel {...settings}>
        <Wrap>
            <a>
                <img src="React-projects-master\Abodbnb\src\components\wildlife.jpg" alt="" />
            </a>
        </Wrap>
        <Wrap>
            <a>
                <img src="C:\Users\animi\Downloads\React-projects-master\React-projects-master\Abodbnb\src\components\temple.jpg" alt="" />
            </a>
        </Wrap>
        <Wrap>
            <a>
                <img src="beach.jpg" alt="" />
            </a>
        </Wrap>
        <Wrap>
            <a>
                <img src="culture.jpg" alt="" />
            </a>
        </Wrap>
    </Carousel>
  )
}

export default ImgSlider

const Carousel = styled(Slider)`
    margin-top: 20px;
    ul li button{
        &:before{
            font-size: 10px;
            color: rgb(150, 158, 171);
        }
    }
    li.slick-active button:before{
        color: white;
    }
    .slick-list{
        overflow: visible;
    }
    button{
        z-index: 1;
    }
`
