const Wrap = styled.div`
    cursor: pointer;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 440px;
    width: 100%;
    overflow: hidden;
    border: 8px solid transparent;
    border-radius: 12px;
    box-shadow: rgb(0 0 0 / 29%) 0px 26px 30px -10px,
    rgb(0 0 0 / 53%) 0px 8px 5px -10px;
    &:hover {
        border: 4px solid rgba(249, 249, 249, 0.8);
    }
    img {
        height: 100%;
        width: 100%;
        object-fit: cover;
    }
`;
